import React, { useState } from "react";
import { View, Text, TextInput, TouchableOpacity, StyleSheet, Image } from "react-native";
import { useNavigation } from "@react-navigation/native";


import CreateAccount from "./CreateAccount";
import social from "../../assets/social.svg";

const Home = ({ navigation }) => {

    return (

        <View style={styles.container}>
            <View style={styles.innercontainer}>
                <View style={{ height: 200 }}>
                    <Text style={styles.title}>Log In</Text>
                    <Text style={{ color: 'gray' }}>Hi! Welcome</Text>
                </View>

                <View>
                    <Text style={{ textAlign: 'left', fontSize: 10 }}>Email Address</Text>
                    <TextInput style={styles.field} placeholder="Enter your email"></TextInput>
                    <Text style={{ textAlign: 'left', fontSize: 10 }}>Password</Text>
                    <TextInput style={styles.field} placeholder="Enter your passowrd"></TextInput>
                </View>

                <View style={styles.button}>
                    <TouchableOpacity >
                        <Text>Log In</Text>
                    </TouchableOpacity>
                </View>

                <View style={{ textAlign: 'center' }}>
                    <Text>Forgotten your passowrd ?</Text>
                    <Text style={{ color: "#C4C4C4" }}>-- Or sign in with --</Text>
                </View>

                {/* <Image source={{social}}/> */}

                <Text style={{ color: 'gray' }}> Don't have an account ?</Text>
                <TouchableOpacity onPress={() => navigation.navigate('CreateAccount')}>
                    <Text style={{ color: "#41C485" }}>CreateAccount</Text>
                </TouchableOpacity>



            </View>

        </View>
    )

}

const styles = StyleSheet.create({
    container: {

        flex: 1,
        justifyContent: "center",
        alignItems: "center",
    },
    innercontainer: {
        width: 390,
        height: 840,
        alignItems: 'center',
        justifyContent: 'center',
    },
    field: {
        borderBottomWidth: 1,
        borderBottomColor: 'black',
        marginBottom: 10,
        color: "#C4C4C4"
    },
    title: {
        color: "#14304",
    },
    button: {
        backgroundColor: "#41C485",
        color: 'white',
        borderRadius: 16,
        width: 300,
        height: 30,
        alignItems: "center",
        justifyContent: "center",
        marginTop: 20,
        marginBottom: 20,
    }


});

export default Home;
