import React, { useState } from "react";
import { View, Text, TextInput, TouchableOpacity, StyleSheet } from "react-native";
import { useNavigation } from "@react-navigation/native";


import CreateAccount from "./CreateAccount";

const Home = ({ navigation }) => {

    return (

        <View style={styles.container}>
            <Text>Log In</Text>
            <Text>Hi! Welcome</Text>

            <TextInput style={styles.field} placeholder="Enter your email"></TextInput>

            <TextInput style={styles.field} placeholder="Enter your passowrd"></TextInput>

            <TouchableOpacity>
                <Text>Log In</Text>
            </TouchableOpacity>

            <Text>Forgotten your passowrd ?</Text>
            <Text>-- Or sign in with --</Text>

            {/* icone/ imagem dos  metodos de logar

 */}

            <Text> Don't have an account ?</Text>
            <TouchableOpacity onPress={() => navigation.navigate('CreateAccount')}>
                <Text>CreateAccount</Text>
            </TouchableOpacity>



        </View>


    )

}

const styles = StyleSheet.create({
    container: {

    },
    field: {
    
    borderColor: "#ccc",
    borderRadius: 50,
    }


});

export default Home;
