import React, { useState } from "react";
import { View, Text, TextInput, TouchableOpacity, StyleSheet } from "react-native";
import { useNavigation } from "@react-navigation/native";


import CreateAccount from "./CreateAccount";

const Home = ({ navigation }) => {

    return (

        <View style={styles.container}>
            <View style={styles.innercontainer}>
                <View>
                    <Text style={styles.title}>Log In</Text>
                    <Text>Hi! Welcome</Text>
                </View>

                <View>
                    <TextInput style={styles.field} placeholder="Enter your email"></TextInput>

                    <TextInput style={styles.field} placeholder="Enter your passowrd"></TextInput>
                </View>

                <View>
                    <TouchableOpacity style={styles.button}>
                        <Text>Log In</Text>
                    </TouchableOpacity>
                </View>

                <View>
                    <Text>Forgotten your passowrd ?</Text>
                    <Text style={{color: "#C4C4C4" }}>-- Or sign in with --</Text>
                </View>

                <Text> Don't have an account ?</Text>
                <TouchableOpacity onPress={() => navigation.navigate('CreateAccount')}>
                    <Text>CreateAccount</Text>
                </TouchableOpacity>



            </View>

        </View>
    )

}

const styles = StyleSheet.create({
    container: {

        flex: 1,
        justifyContent: "center",
        alignItems: "center",
    },
    innercontainer: {
        width: 350,
        height: 1000,
        alignItems: 'center',
        justifyContent: 'center',
    },
    field: {
        borderBottomWidth: 1,
    borderBottomColor: 'black',
    marginBottom: 10,
        color: "#C4C4C4"
    },
    title: {
        color: 'blue',
    },
    button: {
        backgroundColor: "#41C485",
        color: 'white',
        borderRadius: 16,
        width:70,
        height: 30,
        textAlign: "center"
    }
    

});

export default Home;
